import { StyleSheet } from "react-native";

export const styles = StyleSheet.create({
  drink: {
    marginBottom: 40,
    flexDirection: "row",
    alignItems: "center",
  },
  drinkImg: {
    marginHorizontal: 20,
    height: 100,
    width: 101,
  },
  drinkName: {
    height: 19,
    fontFamily: "Roboto",
    fontSize: 16,
    lineHeight: 19,
    color: "#7E7E7E",
  },
});
